    //   title,
    //   completed: false
    // }
    // this.setState({
    //   todos: [...this.state.todos, newTodo]
    // })
  }

  render() {
    return (
      <div className="App">
        <div className="container">
          <Header/>
          <AddTodo addTodo = {this.addTodo}/>
          <Todos todos = {this.state.todos}
            markComplete = {this.markComplete}
            delTodo = {this.delTodo}
          />
        </div>
      </div>
    );
  }

}

export default App;
