import React, { Component } from 'react';
import Todos from './components/Todos'
import './App.css';

class App extends Component {
  state = {
    todos: [
      {
        id: 1,
        title: 'Trash',
        completed: false
      },
      {
        id: 2,
        title: 'Tip',
        completed: true
      },
      {
        id: 3,
        title: 'Hello',
        completed: false
      }
    ]
  }

  markComplete = (id) => {
    this.setState({
      todos: this.state.todos.map(
        todo => {
          if(todo.id == id) {
            todo.completed = !todo.completed;
          } 
          return todo;
        }
      )
    });
  };

  delTodo = (id) => {
    console.log("delTodo working")
  }

  render() {
    return (
      <div className="App">
        <Todos todos = {this.state.todos} 
          markComplete = {this.markComplete} 
          delTodo = {this.delTodo}
        />
      </div>
    );
  }

}

export default App;
