import React, { Component } from 'react'

export class TodoItem extends Component {

    getStyle = () => {
        if(this.props.todo.completed) {
            textDecoration: 'line-through'
        } else {
            textDecoration: 'none'
        }
    }

    render() {
        return (
            <div style = {this.getStyle()}>
                { this.props.todo.title }
            </div>
        )
    }
}

export default TodoItem
