import React, { Component } from 'react'

export class TodoItem extends Component {

    getStyle = () => {
        return {
            background: '#f4f4f4',

            textDecoration: this.props.todo.completed ? 
                'line-through' : 'none'
        }
    }

    render() {
        return (
            <div style = {this.getStyle()}>
                { this.props.todo.title }
            </div>
        )
    }
}

export default TodoItem
